import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
  prevProps: NetworkProps | null;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  private backgroundCanvas: HTMLCanvasElement | null = null;
  private backgroundImage: HTMLImageElement | null = null;
  private animationFrameId: number | null = null;
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([]),
    prevProps: null
  };

  public componentDidMount() {
    console.log("Component mounted");
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  public componentDidUpdate() {
    const props = this.props.args as NetworkProps;
    const prevProps = this.state.prevProps;

    console.log("Component updating");
    console.log("Background image present:", !!props.background_image);
    console.log("Previous background image present:", !!prevProps?.background_image);
    
    if (props.background_image) {
      console.log("Background image length:", props.background_image.length);
    }

    // Only update if props actually changed
    if (prevProps === null || 
        JSON.stringify(prevProps.nodes) !== JSON.stringify(props.nodes) ||
        JSON.stringify(prevProps.edges) !== JSON.stringify(props.edges) ||
        prevProps.background_image !== props.background_image) {
      
      console.log("Props changed, updating component");
      
      if (props.background_image !== prevProps?.background_image) {
        console.log("Background image changed, setting up new background");
        this.setupBackground(props.background_image);
      } else {
        this.setupNetwork();
      }
      
      // Update prevProps in state
      this.setState({ prevProps: props });
    }
  }

  public componentWillUnmount() {
    console.log("Component unmounting");
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
    }
    if (this.state.network) {
      this.state.network.destroy();
    }
  }

  private setupBackground(backgroundImage?: string) {
    if (!this.container.current) {
      console.log("Container not ready");
      return;
    }

    console.log("Setting up background, image data present:", !!backgroundImage);

    // Clear existing canvas if any
    if (this.backgroundCanvas) {
      console.log("Removing existing canvas");
      this.backgroundCanvas.remove();
      this.backgroundCanvas = null;
    }

    if (backgroundImage) {
      console.log("Creating new canvas");
      // Create canvas
      this.backgroundCanvas = document.createElement('canvas');
      this.backgroundCanvas.style.position = 'absolute';
      this.backgroundCanvas.style.left = '0px';
      this.backgroundCanvas.style.top = '0px';
      this.backgroundCanvas.style.zIndex = '-1';
      
      // Set canvas size
      this.backgroundCanvas.width = this.container.current.clientWidth;
      this.backgroundCanvas.height = this.container.current.clientHeight;
      console.log("Canvas size:", this.backgroundCanvas.width, this.backgroundCanvas.height);
      
      // Add canvas to container
      this.container.current.appendChild(this.backgroundCanvas);

      // Create and load image
      this.backgroundImage = new Image();
      
      this.backgroundImage.onload = () => {
        console.log("Image loaded successfully");
        console.log("Image size:", this.backgroundImage.width, this.backgroundImage.height);
        this.startBackgroundRender();
      };

      this.backgroundImage.onerror = (e) => {
        console.error("Error loading image:", e);
      };
      
      // Set image source with base64 data
      const imageUrl = backgroundImage.startsWith('data:image/')
        ? backgroundImage
        : `data:image/png;base64,${backgroundImage}`;
      
      console.log("Setting image source, length:", imageUrl.length);
      this.backgroundImage.src = imageUrl;
    } else {
      console.log("No background image provided");
    }
  }

  private renderBackground = () => {
    if (!this.backgroundCanvas || !this.backgroundImage || !this.state.network) {
      console.log("Missing required elements for background render");
      return;
    }
    
    const ctx = this.backgroundCanvas.getContext('2d');
    if (!ctx) {
      console.log("Could not get canvas context");
      return;
    }

    // Get current network view state
    const position = this.state.network.getViewPosition();
    const scale = this.state.network.getScale();

    // Calculate dimensions
    const srcWidth = this.backgroundCanvas.width / scale;
    const srcHeight = this.backgroundCanvas.height / scale;
    const imgCenterX = ((this.backgroundImage.width / 2) - srcWidth / 2) + position.x;
    const imgCenterY = ((this.backgroundImage.height / 2) - srcHeight / 2) + position.y;

    // Clear and draw
    ctx.clearRect(0, 0, this.backgroundCanvas.width, this.backgroundCanvas.height);
    ctx.drawImage(
      this.backgroundImage,
      imgCenterX,
      imgCenterY,
      srcWidth,
      srcHeight,
      0,
      0,
      this.backgroundCanvas.width,
      this.backgroundCanvas.height
    );

    // Continue animation
    this.animationFrameId = requestAnimationFrame(this.renderBackground);
  };

  private startBackgroundRender() {
    console.log("Starting background render");
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
    }
    this.renderBackground();
  }

  private setupNetwork() {
    if (!this.container.current) return;

    const props = this.props.args as NetworkProps;

    // Clear existing network
    if (this.state.network) {
      this.state.network.destroy();
    }

    // Create new datasets
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    // Create network with merged options
    const network = new Network(
      this.container.current,
      { nodes, edges },
      {
        ...props.options,
        height: '600px',
        autoResize: true,
        layout: {
          randomSeed: 1,
          improvedLayout: true
        }
      }
    );

    // Set up event handlers
    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions()
      });
    });

    this.setState({ network, nodes, edges }, () => {
      if (props.background_image) {
        this.setupBackground(props.background_image);
      }
    });
  }

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
      const pos = this.state.network!.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  public render() {
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: '600px',
          minHeight: '600px',
          border: '1px solid #ddd',
          borderRadius: '4px',
          backgroundColor: '#ffffff',
          position: 'relative',
          overflow: 'hidden'
        }}
      />
    );
  }
}

export default withStreamlitConnection(VisNetwork);
