import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  private backgroundCanvas: HTMLCanvasElement | null = null;
  private backgroundImage: HTMLImageElement | null = null;
  private animationFrameId: number | null = null;
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([])
  };

  public componentDidMount() {
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  public componentDidUpdate(prevProps: any) {
    const props = this.props.args as NetworkProps;
    if (props.background_image !== prevProps?.args?.background_image) {
      this.setBackgroundMap(props.background_image);
    }
  }

  public componentWillUnmount() {
    if (this.animationFrameId) {
      cancelAnimationFrame(this.animationFrameId);
    }
    if (this.state.network) {
      this.state.network.destroy();
    }
  }

  private setupNetwork() {
    if (!this.container.current) return;

    const props = this.props.args as NetworkProps;
    
    // Create new datasets
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    // Create network
    const network = new Network(
      this.container.current,
      { nodes, edges },
      {
        ...props.options,
        height: '600px',
      }
    );

    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions()
      });
    });

    this.setState({ network, nodes, edges }, () => {
      if (props.background_image) {
        this.setBackgroundMap(props.background_image);
      }
    });
  }

  private setBackgroundMap(backgroundImage?: string) {
    if (!backgroundImage || !this.state.network || !this.container.current) return;

    // Clear existing
    if (this.animationFrameId) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = null;
    }
    if (this.backgroundCanvas) {
      this.backgroundCanvas.remove();
    }

    // Create new canvas
    const canvas = document.createElement('canvas');
    this.backgroundCanvas = canvas;
    canvas.style.position = 'absolute';
    canvas.style.left = '0';
    canvas.style.top = '0';
    canvas.style.zIndex = '-1';

    // Set dimensions
    canvas.width = this.container.current.clientWidth;
    canvas.height = this.container.current.clientHeight;

    const img = new Image();
    img.onload = () => {
      this.backgroundImage = img;
      this.container.current?.appendChild(canvas);
      this.renderBackground();
    };

    const imageUrl = backgroundImage.startsWith('data:image/')
      ? backgroundImage
      : `data:image/png;base64,${backgroundImage}`;
    img.src = imageUrl;
  }

  private renderBackground = () => {
    if (!this.backgroundCanvas || !this.backgroundImage || !this.state.network) return;
    
    const ctx = this.backgroundCanvas.getContext('2d');
    if (!ctx) return;

    const position = this.state.network.getViewPosition();
    const scale = this.state.network.getScale();
    
    const srcWidth = this.backgroundCanvas.width / scale;
    const srcHeight = this.backgroundCanvas.height / scale;
    const imgCenterX = ((this.backgroundImage.width / 2) - srcWidth / 2) + position.x;
    const imgCenterY = ((this.backgroundImage.height / 2) - srcHeight / 2) + position.y;

    ctx.clearRect(0, 0, this.backgroundCanvas.width, this.backgroundCanvas.height);
    ctx.drawImage(
      this.backgroundImage,
      imgCenterX,
      imgCenterY,
      srcWidth,
      srcHeight,
      0,
      0,
      this.backgroundCanvas.width,
      this.backgroundCanvas.height
    );

    this.animationFrameId = requestAnimationFrame(this.renderBackground);
  };

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
      const pos = this.state.network!.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  public render() {
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: '600px',
          border: '1px solid #ddd',
          position: 'relative',
          overflow: 'hidden',
          backgroundColor: '#ffffff'
        }}
      />
    );
  }
}

export default withStreamlitConnection(VisNetwork);
