import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
  viewport?: {
    position: {
      x: number;
      y: number;
    };
    scale: number;
  };
  updates?: {
    nodeId: string;
    nodeData: any;
  };
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
  viewportInitialized: boolean;
  isStabilized: boolean;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  private networkContainer = React.createRef<HTMLDivElement>();
  private backgroundCanvas = React.createRef<HTMLCanvasElement>();
  private backgroundImage: HTMLImageElement | null = null;
  private animationFrame: number | null = null;
  private viewportUpdateTimeout: NodeJS.Timeout | null = null;
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([]),
    viewportInitialized: false,
    isStabilized: false
  };

  public componentDidMount() {
    console.log("Component mounted");
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  public componentDidUpdate(prevProps: any) {
    const props = this.props.args as NetworkProps;
    const prevNetworkProps = prevProps.args as NetworkProps;
    
    // Handle network data updates
    if (this.state.network) {
      // Check if we have specific node updates
      if (props.updates && props.updates !== prevNetworkProps.updates) {
        console.log("Applying node update:", props.updates);
        this.handleNodeUpdate(props.updates);
      }
      // Otherwise handle full data updates
      else if (props.nodes !== prevNetworkProps.nodes || props.edges !== prevNetworkProps.edges) {
        console.log("Updating full network data");
        const nodes = new DataSet(props.nodes);
        const edges = new DataSet(props.edges);
        this.state.network.setData({ nodes, edges });
        this.setState({ nodes, edges });
      }
    }

    // Handle background image updates
    if (props.background_image && (!this.backgroundImage || this.backgroundImage.src !== props.background_image)) {
      this.setupBackgroundImage(props.background_image);
    }
  }

  private handleNodeUpdate(update: { nodeId: string; nodeData: any }) {
    if (!this.state.nodes) return;

    try {
      // Get current position and selection state
      const position = this.state.network?.getPosition(update.nodeId);
      const isSelected = this.state.network?.getSelectedNodes().includes(update.nodeId);

      // Update the node in the DataSet
      this.state.nodes.update({
        id: update.nodeId,
        ...update.nodeData,
        x: position?.x,
        y: position?.y,
        fixed: true  // Temporarily fix position
      });

      // Unfix position after a brief delay
      setTimeout(() => {
        this.state.nodes.update({
          id: update.nodeId,
          fixed: false
        });
      }, 100);

      // Restore selection if needed
      if (isSelected) {
        this.state.network?.selectNodes([update.nodeId], true);
      }

      console.log(`Node ${update.nodeId} updated successfully`);
    } catch (error) {
      console.error("Error updating node:", error);
    }
  }

  private setupNetwork() {
    if (!this.networkContainer.current) return;

    const props = this.props.args as NetworkProps;
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    const network = new Network(
      this.networkContainer.current,
      { nodes, edges },
      {
        ...props.options,
        nodes: {
          ...props.options.nodes,
          shape: 'custom',  // Enable custom shapes
          ctxRenderer: {
            template: (node: any) => {
              return node.ctxRenderer?.template || '';
            }
          }
        }
      }
    );

    // Set up event handlers
    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('stabilized', () => {
      console.log("Network stabilized");
      if (!this.state.isStabilized && props.viewport) {
        network.moveTo({
          position: props.viewport.position,
          scale: props.viewport.scale,
          animation: false
        });
        this.setState({ isStabilized: true, viewportInitialized: true });
      }
    });

    network.on('dragEnd', () => {
      if (this.viewportUpdateTimeout) {
        clearTimeout(this.viewportUpdateTimeout);
      }

      this.viewportUpdateTimeout = setTimeout(() => {
        if (this.state.isStabilized) {
          Streamlit.setComponentValue({
            selected: network.getSelectedNodes(),
            positions: this.getNodePositions(),
            viewport: {
              scale: network.getScale(),
              position: network.getViewPosition()
            }
          });
        }
      }, 100);
    });

    network.on('zoom', () => {
      if (this.viewportUpdateTimeout) {
        clearTimeout(this.viewportUpdateTimeout);
      }

      this.viewportUpdateTimeout = setTimeout(() => {
        if (this.state.isStabilized) {
          Streamlit.setComponentValue({
            selected: network.getSelectedNodes(),
            positions: this.getNodePositions(),
            viewport: {
              scale: network.getScale(),
              position: network.getViewPosition()
            }
          });
        }
      }, 100);
    });

    this.setState({ network, nodes, edges });
  }

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
        const pos = this.state.network!.getPosition(node.id);
        positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  public componentWillUnmount() {
    if (this.state.network) {
        this.state.network.destroy();
    }
    if (this.animationFrame) {
        cancelAnimationFrame(this.animationFrame);
    }
    if (this.viewportUpdateTimeout) {
        clearTimeout(this.viewportUpdateTimeout);
    }
  }

  private setupBackgroundImage(imageUrl: string) {
    const img = new Image();
    img.onload = () => {
      console.log("Background image loaded", { width: img.width, height: img.height });
      this.backgroundImage = img;
      this.startRenderLoop();
    };
    img.onerror = (e) => {
      console.error("Error loading background image:", e);
    };
    img.src = imageUrl;
  }

  private startRenderLoop() {
    if (this.animationFrame) {
      cancelAnimationFrame(this.animationFrame);
    }
    this.renderBackground();
  }

  private renderBackground() {
    if (!this.backgroundCanvas.current || !this.backgroundImage || !this.state.network) return;
    
    const canvas = this.backgroundCanvas.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Get current viewport state
    const position = this.state.network.getViewPosition();
    const scale = this.state.network.getScale();

    // Ensure canvas dimensions match container
    const containerRect = canvas.getBoundingClientRect();
    canvas.width = containerRect.width;
    canvas.height = containerRect.height;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Calculate dimensions based on scale
    const srcWidth = canvas.width / scale;
    const srcHeight = canvas.height / scale;
    const imgCenterX = ((this.backgroundImage.width / 2) - srcWidth / 2) + position.x;
    const imgCenterY = ((this.backgroundImage.height / 2) - srcHeight / 2) + position.y;

    try {
      ctx.drawImage(
        this.backgroundImage,
        imgCenterX,
        imgCenterY,
        srcWidth,
        srcHeight,
        0,
        0,
        canvas.width,
        canvas.height
      );
    } catch (error) {
      console.error("Error rendering background:", error);
    }

    this.animationFrame = requestAnimationFrame(() => this.renderBackground());
  }

  public render() {
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: '600px',
          position: 'relative',
          border: '1px solid #ddd',
          overflow: 'hidden'
        }}
      >
        <canvas
          ref={this.backgroundCanvas}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            zIndex: 0,
            opacity: 0.3
          }}
        />
        <div 
          ref={this.networkContainer}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            zIndex: 1
          }}
        />
      </div>
    );
  }
}

export default withStreamlitConnection(VisNetwork);
