//streamlit_vis/frontend/src/VisNetwork.tsx

import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
  prevProps: NetworkProps | null;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([]),
    prevProps: null
  };

  public componentDidMount() {
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  public componentDidUpdate() {
    const props = this.props.args as NetworkProps;
    const prevProps = this.state.prevProps;

    // Only update if props actually changed
    if (prevProps === null || 
        JSON.stringify(prevProps.nodes) !== JSON.stringify(props.nodes) ||
        JSON.stringify(prevProps.edges) !== JSON.stringify(props.edges) ||
        prevProps.background_image !== props.background_image) {
      
      if (props.background_image !== prevProps?.background_image) {
        this.updateBackgroundImage();
      } else {
        this.setupNetwork();
      }
      
      // Update prevProps in state
      this.setState({ prevProps: props });
    }
  }

  private setupNetwork() {
    if (!this.container.current) return;

    const props = this.props.args as NetworkProps;

    // Clear existing network
    if (this.state.network) {
      this.state.network.destroy();
    }

    // Create new datasets
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    // Create network with merged options
    const network = new Network(
      this.container.current,
      { nodes, edges },
      {
        ...props.options,
        height: '600px',
        autoResize: true,
        layout: {
          randomSeed: 1,
          improvedLayout: true
        }
      }
    );

    // Set up event handlers
    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions()
      });
    });

    network.once('afterDrawing', () => {
      network.fit();
    });

    // Update state without triggering another update
    this.setState({ 
      network, 
      nodes, 
      edges
    });
  }

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
      const pos = this.state.network!.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  private updateBackgroundImage() {
    if (!this.container.current) return;

    const props = this.props.args as NetworkProps;
    if (props.background_image) {
        // Create a wrapper div for background if it doesn't exist
        let bgDiv = this.container.current.querySelector('.network-background') as HTMLDivElement;
        if (!bgDiv) {
            bgDiv = document.createElement('div');
            bgDiv.className = 'network-background';
            bgDiv.style.position = 'absolute';
            bgDiv.style.top = '0';
            bgDiv.style.left = '0';
            bgDiv.style.right = '0';
            bgDiv.style.bottom = '0';
            bgDiv.style.zIndex = '-1';
            this.container.current.insertBefore(bgDiv, this.container.current.firstChild);
        }
        
        bgDiv.style.backgroundImage = `url(data:image/png;base64,${props.background_image})`;
        bgDiv.style.backgroundSize = 'cover';
        bgDiv.style.backgroundPosition = 'center';
        bgDiv.style.opacity = '0.3';  // Make background subtle
    } else {
        // Remove background div if it exists
        const bgDiv = this.container.current.querySelector('.network-background');
        if (bgDiv) {
            bgDiv.remove();
        }
    }
	}

	public render() {
		return (
			<div
				ref={this.container}
				style={{
					width: '100%',
					height: '600px',
					minHeight: '600px',
					border: '1px solid #ddd',
					borderRadius: '4px',
					backgroundColor: '#ffffff',
					position: 'relative',
					overflow: 'hidden'
				}}
			/>
		);
	}
}

export default withStreamlitConnection(VisNetwork);
