import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
  shouldShowBackground: boolean;  // Added this
  backgroundSrc: string;         // Added this
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  private networkContainer = React.createRef<HTMLDivElement>();
  private backgroundDiv = React.createRef<HTMLDivElement>();
  private backgroundImage: HTMLImageElement | null = null;
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([]),
    shouldShowBackground: false,
    backgroundSrc: ''
  };

  public componentDidMount() {
    console.log("Component mounted");
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  public componentDidUpdate() {
    const props = this.props.args as NetworkProps;
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);
    
    if (this.state.network) {
      this.state.network.setData({ nodes, edges });
    }

    // Handle background image changes
    if (props.background_image && props.background_image !== this.state.backgroundSrc) {
      console.log("New background image detected");
      this.setState({
        shouldShowBackground: true,
        backgroundSrc: props.background_image
      });
    }
  }

  public componentWillUnmount() {
    if (this.state.network) {
      this.state.network.destroy();
    }
  }

  private createNetworkContainer() {
    if (!this.container.current) {
      console.log("Main container not ready");
      return;
    }

    // Create a container for the network
    const networkDiv = document.createElement('div');
    networkDiv.style.width = '100%';
    networkDiv.style.height = '100%';
    networkDiv.style.position = 'relative';
    networkDiv.style.zIndex = '1';
    
    // Create a container for the background
    const backgroundDiv = document.createElement('div');
    backgroundDiv.style.position = 'absolute';
    backgroundDiv.style.top = '0';
    backgroundDiv.style.left = '0';
    backgroundDiv.style.width = '100%';
    backgroundDiv.style.height = '100%';
    backgroundDiv.style.zIndex = '0';
    backgroundDiv.style.pointerEvents = 'none';
    this.backgroundDiv = backgroundDiv;

    // Add both to the main container
    this.container.current.appendChild(backgroundDiv);
    this.container.current.appendChild(networkDiv);

    // Store network div as the current value of the ref
    if (this.networkContainer.current) {
      this.networkContainer.current.appendChild(networkDiv);
    }
  }

  private setupNetwork() {
    if (!this.networkContainer.current) {
      console.error("Network container not found");
      return;
    }

    const props = this.props.args as NetworkProps;
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    const network = new Network(
      this.networkContainer.current,
      { nodes, edges },
      {
        ...props.options,
        height: '600px',
      }
    );

    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions(network, nodes)
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions(network, nodes)
      });
    });

    this.setState({ network, nodes, edges });
  }


  private setupBackground(backgroundImage: string) {
    console.log("Setting up background with image data length:", backgroundImage.length);
    
    if (!this.backgroundDiv) {
      console.error("Background div not ready");
      return;
    }

    // Clear existing background
    this.backgroundDiv.innerHTML = '';

    // Create a container div for positioning
    const container = document.createElement('div');
    container.style.position = 'absolute';
    container.style.top = '0';
    container.style.left = '0';
    container.style.right = '0';
    container.style.bottom = '0';
    container.style.display = 'flex';
    container.style.justifyContent = 'center';
    container.style.alignItems = 'center';
    container.style.overflow = 'hidden';

    // Create and setup image
    const img = document.createElement('img');
    img.style.width = '100%';
    img.style.height = '100%';
    img.style.objectFit = 'contain';
    img.style.opacity = '0.3';

    // Log all steps
    console.log("Created image element");
    
    img.onload = () => {
      console.log("Image loaded successfully", {
        width: img.width,
        height: img.height,
        naturalWidth: img.naturalWidth,
        naturalHeight: img.naturalHeight
      });
    };

    img.onerror = (error) => {
      console.error("Error loading image:", {
        error,
        src: img.src.substring(0, 100) + '...' // Log truncated src for debugging
      });
    };

    try {
      img.src = backgroundImage;
      container.appendChild(img);
      this.backgroundDiv.appendChild(container);
      console.log("Added image to DOM");
    } catch (error) {
      console.error("Error setting up background:", error);
    }
  }

  private getNodePositions(network: Network, nodes: DataSet<any>) {
    const positions: { [key: string]: { x: number, y: number } } = {};
    nodes.forEach((node) => {
      const pos = network.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    return positions;
  }

  public render() {
    const props = this.props.args as NetworkProps;
    
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: '600px',
          border: '1px solid #ddd',
          position: 'relative',
          overflow: 'hidden',
          backgroundColor: '#ffffff'
        }}
      >
        {this.state.shouldShowBackground && (
          <div 
            ref={this.backgroundDiv}
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              zIndex: 0,
              opacity: 0.3,
              pointerEvents: 'none',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              overflow: 'hidden'
            }}
          >
            <img 
              src={this.state.backgroundSrc}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'contain'
              }}
              alt="Background"
              onLoad={() => {
                console.log("Background image loaded successfully");
              }}
              onError={(e) => {
                console.error("Error loading background image:", e);
              }}
            />
          </div>
        )}
        <div 
          ref={this.networkContainer}
          style={{
            position: 'relative',
            width: '100%',
            height: '100%',
            zIndex: 1
          }}
        />
      </div>
    );
  }
}

export default withStreamlitConnection(VisNetwork);
