import React from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface NetworkProps {
  nodes: any[];
  edges: any[];
  options: any;
  background_image?: string;
  height?: string;
}

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  private networkContainer: HTMLDivElement | null = null;
  private backgroundDiv: HTMLDivElement | null = null;

  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([])
  };

  public componentDidMount() {
    console.log("Component mounted");
    this.createNetworkContainer();
    Streamlit.setFrameHeight(600);
    this.setupNetwork();
  }

  private createNetworkContainer() {
    if (!this.container.current) {
      console.log("Main container not ready");
      return;
    }

    // Create a container for the network
    const networkDiv = document.createElement('div');
    networkDiv.style.width = '100%';
    networkDiv.style.height = '100%';
    networkDiv.style.position = 'relative';
    networkDiv.style.zIndex = '1';
    this.networkContainer = networkDiv;

    // Create a container for the background
    const backgroundDiv = document.createElement('div');
    backgroundDiv.style.position = 'absolute';
    backgroundDiv.style.top = '0';
    backgroundDiv.style.left = '0';
    backgroundDiv.style.width = '100%';
    backgroundDiv.style.height = '100%';
    backgroundDiv.style.zIndex = '0';
    backgroundDiv.style.pointerEvents = 'none';
    this.backgroundDiv = backgroundDiv;

    // Add both to the main container
    this.container.current.appendChild(backgroundDiv);
    this.container.current.appendChild(networkDiv);
  }

  private setupNetwork() {
    if (!this.networkContainer) {
      console.log("Network container not ready");
      return;
    }

    const props = this.props.args as NetworkProps;
    console.log("Setting up network with props:", props);
    
    const nodes = new DataSet(props.nodes);
    const edges = new DataSet(props.edges);

    const network = new Network(
      this.networkContainer,
      { nodes, edges },
      {
        ...props.options,
        height: '600px',
      }
    );

    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions()
      });
    });

    this.setState({ network, nodes, edges }, () => {
      if (props.background_image) {
        this.setupBackground(props.background_image);
      }
    });
  }

  private setupBackground(backgroundImage: string) {
    console.log("Setting up background with image:", backgroundImage);
    
    if (!this.backgroundDiv) {
      console.log("Background div not ready");
      return;
    }

    // Clear existing background
    while (this.backgroundDiv.firstChild) {
      this.backgroundDiv.removeChild(this.backgroundDiv.firstChild);
    }

    // Create and setup image
    const img = new Image();
    img.style.width = '100%';
    img.style.height = '100%';
    img.style.objectFit = 'cover';
    img.style.opacity = '0.3';

    img.onload = () => {
      console.log("Background image loaded successfully");
      this.backgroundDiv?.appendChild(img);
    };

    img.onerror = (error) => {
      console.error("Error loading background image:", error);
    };

    try {
      if (backgroundImage.startsWith('data:image/') || backgroundImage.startsWith('http')) {
        img.src = backgroundImage;
      } else {
        img.src = `data:image/png;base64,${backgroundImage}`;
      }
    } catch (error) {
      console.error("Error setting image source:", error);
    }
  }

  private renderBackground = () => {
    if (!this.backgroundCanvas || !this.backgroundImage || !this.state.network) {
      console.log("Missing required elements for render:", {
        hasCanvas: !!this.backgroundCanvas,
        hasImage: !!this.backgroundImage,
        hasNetwork: !!this.state.network
      });
      return;
    }
    
    const ctx = this.backgroundCanvas.getContext("2d");
    if (!ctx) {
      console.log("Could not get canvas context");
      return;
    }

    try {
      const position = this.state.network.getViewPosition();
      const scale = this.state.network.getScale();
      
      const srcWidth = this.backgroundCanvas.width / scale;
      const srcHeight = this.backgroundCanvas.height / scale;
      
      const imgCenterX = ((this.backgroundImage.width / 2) - srcWidth / 2) + position.x;
      const imgCenterY = ((this.backgroundImage.height / 2) - srcHeight / 2) + position.y;

      ctx.clearRect(0, 0, this.backgroundCanvas.width, this.backgroundCanvas.height);
      
      console.log("Drawing image with parameters:", {
        imgCenterX,
        imgCenterY,
        srcWidth,
        srcHeight,
        canvasWidth: this.backgroundCanvas.width,
        canvasHeight: this.backgroundCanvas.height
      });

      ctx.drawImage(
        this.backgroundImage, 
        imgCenterX, 
        imgCenterY, 
        srcWidth, 
        srcHeight,
        0,
        0,
        this.backgroundCanvas.width,
        this.backgroundCanvas.height
      );

      this.renderLoop = requestAnimationFrame(this.renderBackground);
    } catch (error) {
      console.error("Error in renderBackground:", error);
    }
  };

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
      const pos = this.state.network!.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  public render() {
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: '600px',
          border: '1px solid #ddd',
          position: 'relative',
          overflow: 'hidden',
          backgroundColor: '#ffffff'
        }}
      />
    );
  }
}

export default withStreamlitConnection(VisNetwork);
