// streamlit_vis/frontend/src/VisNetwork.tsx
import React, { useEffect, useRef } from 'react';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";

interface State {
  network: Network | null;
  nodes: DataSet<any>;
  edges: DataSet<any>;
}

class VisNetwork extends StreamlitComponentBase<State> {
  private container = React.createRef<HTMLDivElement>();
  
  public state: State = {
    network: null,
    nodes: new DataSet([]),
    edges: new DataSet([])
  };

  public componentDidMount() {
    this.setupNetwork();
  }

  public componentDidUpdate(prevProps: any) {
    // Check if data or options changed
    if (
      JSON.stringify(prevProps.args.nodes) !== JSON.stringify(this.props.args.nodes) ||
      JSON.stringify(prevProps.args.edges) !== JSON.stringify(this.props.args.edges) ||
      JSON.stringify(prevProps.args.options) !== JSON.stringify(this.props.args.options)
    ) {
      this.setupNetwork();
    }

    // Handle background image updates
    if (prevProps.args.background_image !== this.props.args.background_image) {
      this.updateBackgroundImage();
    }
  }

  private setupNetwork() {
    if (!this.container.current) return;

    // Clear existing network
    if (this.state.network) {
      this.state.network.destroy();
    }

    // Create new datasets
    const nodes = new DataSet(this.props.args.nodes);
    const edges = new DataSet(this.props.args.edges);

    // Create network
    const network = new Network(
      this.container.current,
      { nodes, edges },
      this.props.args.options
    );

    // Set up event handlers
    network.on('select', (params) => {
      Streamlit.setComponentValue({
        selected: params.nodes,
        positions: this.getNodePositions()
      });
    });

    network.on('dragEnd', () => {
      Streamlit.setComponentValue({
        selected: network.getSelectedNodes(),
        positions: this.getNodePositions()
      });
    });

    // Update state
    this.setState({ network, nodes, edges }, () => {
      this.updateBackgroundImage();
    });
  }

  private getNodePositions() {
    if (!this.state.network || !this.state.nodes) return {};
    
    const positions: { [key: string]: { x: number, y: number } } = {};
    this.state.nodes.forEach((node) => {
      const pos = this.state.network!.getPosition(node.id);
      positions[node.id] = { x: pos.x, y: pos.y };
    });
    
    return positions;
  }

  private updateBackgroundImage() {
    if (!this.container.current || !this.state.network) return;

    const { background_image } = this.props.args;
    if (background_image) {
      this.container.current.style.backgroundImage = `url(${background_image})`;
      this.container.current.style.backgroundSize = 'cover';
      this.container.current.style.backgroundPosition = 'center';
    } else {
      this.container.current.style.backgroundImage = 'none';
    }
  }

  public render() {
    const { height } = this.props.args;
    
    return (
      <div
        ref={this.container}
        style={{
          width: '100%',
          height: height || '600px',
          border: '1px solid #ccc',
          position: 'relative'
        }}
      />
    );
  }
}

export default withStreamlitConnection(VisNetwork);
